let dark = !0;
window.innerWidth < 768 &&
  [].slice
    .call(document.querySelectorAll("[data-bss-disabled-mobile]"))
    .forEach(function (t) {
      t.classList.remove("animated"),
        t.removeAttribute("data-bss-hover-animate"),
        t.removeAttribute("data-aos"),
        t.removeAttribute("data-bss-parallax-bg"),
        t.removeAttribute("data-bss-scroll-zoom");
    }),
  document.addEventListener(
    "DOMContentLoaded",
    function () {
      "AOS" in window && AOS.init();

      document.getElementById("logo").src = "./assets/img/logo.webp";
      if (typeof(Storage) !== "undefined") {
        if(!localStorage.getItem("dark")) {
          localStorage.setItem("dark", dark);
        } else {
          console.log(localStorage.getItem("dark"));
          if(localStorage.getItem("dark") === "false") {
            disableTransition();
            toggle();

            setTimeout(function() {
              enableTransition();
            }, 2000)
          }
        }
      }
    },
    !1
  );
const label = document.getElementById("toggle");
const sun = document.getElementById("sun"), moon = document.getElementById("moon");
const all = document.getElementsByTagName("*");
label.addEventListener("click", () => {
  toggle();
});

function disableTransition() {
  document.body.style.transition = "all 0ms";
}

function enableTransition() {
  document.body.style.transition = "all 500ms";
}

function toggle() {
  if (dark) {
    dark = !1;
    localStorage.setItem("dark", dark);
    
    for (var t = 0; t < all.length; t++)
      all[t].classList.contains("bg-dark") &&
        all[t].classList.replace("bg-dark", "bg-light"),
        all[t].classList.contains("text-light") &&
          all[t].classList.replace("text-light", "text-dark"),
        all[t].classList.contains("btn-outline-light") &&
          all[t].classList.replace("btn-outline-light", "btn-outline-dark");
    sun.hidden = true;
    moon.hidden = false;
  } else {
    dark = !0;
    localStorage.setItem("dark", dark);

    for (t = 0; t < all.length; t++)
      all[t].classList.contains("bg-light") &&
        all[t].classList.replace("bg-light", "bg-dark"),
        all[t].classList.contains("text-dark") &&
          all[t].classList.replace("text-dark", "text-light"),
          all[t].classList.contains("btn-outline-dark") &&
          all[t].classList.replace("btn-outline-dark", "btn-outline-light");
      sun.hidden = false;
      moon.hidden = true;
  }
}
