const result=document.getElementById("result"),button=document.getElementById("submit"),dropdown=document.getElementById("dropdown");function radixSort(t){const e=getMax(t);for(let n=0;n<e;n++){let e=Array.from({length:10},(()=>[]));for(let o=0;o<t.length;o++){e[getDigit(t[o],n)].push(t[o])}t=[].concat(...e)}return t}function radixReverse(t){return radixSort(t).reverse()}function getMax(t){let e=0;for(let n=0;n<t.length;n++)e=Math.max(e,digitCount(t[n]));return e}function getDigit(t,e){return Math.floor(Math.abs(t)/Math.pow(10,e))%10}function digitCount(t){return 0===t?1:Math.floor(Math.log10(Math.abs(t)))+1}function sort(t){console.log("sort()");const e=document.getElementById("input"),n=!/^\d+( \d+)*$/.test(e.value);switch(console.log("text: "+n+", switch(), id:"+t),t.toString()){case"11":if(console.log("11!"),!n){console.log("running");const t=e.value.trim().split(" ").map(Number);if(console.log(t.length),t.every(Number.isInteger)){result.textContent=radixSort(t);break}}break;case"12":if(!n){console.log("running");const t=e.value.trim().split(" ").map(Number);if(console.log(t.length),t.every(Number.isInteger)){result.textContent=radixReverse(t);break}}break;case"13":n&&console.log("running");break;case"14":break;default:result.textContent="Invalid entry"}}console.log("loaded"),button.addEventListener("click",(function(){console.log("running"),sort(dropdown.options[dropdown.selectedIndex].value)}));